import { useState } from 'react';
import './app.css';
import { Backspace } from '@material-ui/icons';
function App() {

  const [result, setResult] = useState("");

  const addItem = (e) => {
    setResult(result.concat(e.target.name));
  }
  const Hclear =() =>{
    setResult("");
  }
  const findResult =() =>{
    setResult(eval(e.target.name));
  }


  return (

    <>
      <div className="container">

        <form>
          <input type="text" value = {result}/>
        </form>

        <div className="keypad">
          <button className='highlight' onClick={Hclear} id='clear'>Clear</button>
          <button className='highlight' onClick={Backspace} id='backspace'>C</button>
          <button className='highlight' name="/" onClick={addItem}>/</button>
          <button name="7" onClick={addItem}>7</button>
          <button name="8" onClick={addItem}>8</button>
          <button name="9" onClick={addItem}>9</button>
          <button className= "highlight" name="*" onClick={addItem}>*</button>
          <button name="4" onClick={addItem}>4</button>
          <button name="5" onClick={addItem}>5</button>
          <button name="6" onClick={addItem}>6</button>
          <button className= "highlight" name="-" onClick={addItem}>-</button>
          <button  name="1" onClick={addItem}>1</button>
          <button name="2" onClick={addItem}>2</button>
          <button name="3" onClick={addItem}>3</button>
          <button className= "highlight" name="+" onClick={addItem}>+</button>
          <button name="0" onClick={addItem}>0</button>
          <button name="." onClick={addItem}>.</button>
          <button className= "highlight" name="=" onClick={findResult} id='result'>=</button>
        </div>
      </div>
    </>
  );
}

export default App;
